import React, {useState} from "react";
import {roundSeparateNumber} from "../../../common/utils";
import {getCripto, getCurrentCripto} from "../../../common/fetch";

const ItemTableCurrency = ({asset}) => {
    const [isOpenDetails, setIsOpenDetails] = useState(false);
    const [assetDetails, setAssetDetails] = useState({});
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    const today  = new Date();
    const date = today.toLocaleDateString("en-US", options);

    const openDetails = () => {
        setIsOpenDetails(!isOpenDetails);
        (async () => {
            const assetsFetch = await getCurrentCripto(asset.id);
            setAssetDetails(assetsFetch);
        })();
    }

    return (
        <>
            <tr onClick={openDetails}>
                <td>{asset.rank}</td>
                <td>{asset.name}</td>
                <td>${roundSeparateNumber(asset.priceUsd)}</td>
                <td>${roundSeparateNumber(asset.marketCapUsd)}</td>
                <td>${roundSeparateNumber(asset.vwap24Hr)}</td>
                <td>${roundSeparateNumber(asset.supply)}</td>
                <td>${roundSeparateNumber(asset.volumeUsd24Hr)}</td>
                <td>{roundSeparateNumber(asset.changePercent24Hr)}%</td>
            </tr>
            {isOpenDetails && <>
                <tr className="subTable__row">
                    <td colSpan={2} rowspan="2">
                        <img src={`https://assets.coincap.io/assets/icons/${asset.symbol.toLowerCase()}@2x.png`}/>
                    </td>
                    <td colSpan={2}>
                        {asset.name} ({asset.symbol})
                    </td>
                    <td colSpan={2}>HIGH</td>
                    <td colSpan={2}>AVERAGE</td>
                </tr>
                    <tr className="subTable__row">
                        <td colSpan={2}>{date}</td>
                        <td colSpan={2}>LOW</td>
                        <td colSpan={2}>CHANGE</td>
                    </tr>
                </>
                }
            </>
                )
            }
;

export default ItemTableCurrency;
