import React from 'react';
import {CartesianGrid, BarChart, Bar, ResponsiveContainer, Tooltip, XAxis, YAxis} from "recharts";

const Chart = ({assets24h, min, max}) => {

    const formatYAxis = (tickItem) => Number(tickItem).toFixed();

    const formatXAxis = (tickItem) => {
        const hour = (new Date(tickItem)).getHours();
        return `${hour}h`;
    };

    return (
        <ResponsiveContainer>
            <BarChart
                // width={100vh}
                // height={400}
                data={assets24h}
                margin={{top: 5, right: 20, left: 10, bottom: 5}}
                useInteractiveGuideline={true}
            >
                <XAxis dataKey="date" tickFormatter={formatXAxis}/>
                <YAxis showMaxMin={false} tickCount={20} tickFormatter={formatYAxis} type="number"
                       domain={[min, max]}/>
                <Tooltip/>
                <CartesianGrid strokeDasharray="3 3"/>
                <Bar activeDot={{r: 8}} type="monotone" dataKey="priceUsd" stroke="#ff7300"/>
                />
            </BarChart>
        </ResponsiveContainer>
    )
};

export default Chart;
