import React from 'react';
import {getDateTime, roundSeparateNumber} from "../../common/utils";
import Chart from "../main/components/Chart.component";
import Loader from "../main/components/Loader.component";
import {useSelector} from "react-redux";

const CurrencyPage = ({id}) => {
    const {assets24h, details} = useSelector(state => state.details);
    const {assets} = useSelector(state => state.assets);
    const {date} = getDateTime();

    const getAsset = () => {
        return assets.find((item) => item.id === id);
    };

    const asset = getAsset();

    return (
        <>
            <tr className="subTable__row">
                <td colSpan={2} rowSpan={2}>
                    <img
                        src={`https://assets.coincap.io/assets/icons/${asset.symbol.toLowerCase()}@2x.png`}
                    alt="currency"/>
                </td>
                <td colSpan={2}>
                    {asset.name} ({asset.symbol})
                </td>
                <td colSpan={2}>HIGH: ${roundSeparateNumber(details.max)}</td>
                <td colSpan={2} rowSpan={2} className="align-middle">AVERAGE:
                    ${roundSeparateNumber(details.average)}</td>
            </tr>
            <tr className="subTable__row">
                <td colSpan={2}>{date}</td>
                <td colSpan={2}>LOW: ${roundSeparateNumber(details.min)}</td>
            </tr>
            <tr>
                <td colSpan={9} className="text-center">
                    {assets24h ?
                        <Chart assets24h={assets24h} max={details.max} min={details.min}/>
                        :
                        <Loader/>}
                </td>
            </tr>
        </>
    )
};

export default CurrencyPage;
