import React from "react";
import {getDateTime, getDetails, roundSeparateNumber} from "../../../common/utils";
import {getCurrentCripto} from "../../../common/fetch";
import {useDispatch, useSelector} from "react-redux";
import Chart from "./Chart.component";
import Loader from "./Loader.component";

const ItemTableWalletCurrency = ({currency, index}) => {
        const dispatch = useDispatch();
        const {assets24h, details, isOpen} = useSelector(state => state.details);

        const price = roundSeparateNumber(currency.priceUsd);
        const summary = roundSeparateNumber(currency.priceUsd * 1);

        return (
            <>
                <tr >
                    <td>{index}</td>
                    <td>{currency.name}</td>
                    <td>${price}</td>
                    <td>1</td>
                    <td>${summary}</td>
                    <td>-</td>
                </tr>

            </>
        );
    }
;

export default ItemTableWalletCurrency;
