import React, {useEffect, useState} from "react";
import {getDetails, roundSeparateNumber} from "../../../common/utils";
import {getCripto, getCurrentCripto} from "../../../common/fetch";
import {
    LineChart,
    AreaChart,
    Bar,
    Brush,
    CartesianGrid,
    ComposedChart,
    Legend,
    Line,
    Pie,
    PieChart,
    ResponsiveContainer,
    Tooltip,
    XAxis,
    YAxis
} from "recharts";
import {useDispatch, useSelector} from "react-redux";

const ItemTableCurrency = ({asset}) => {
        const dispatch = useDispatch();
        const {assetsToday, assets24h, isOpen} = useSelector(state => state.details);

    const [isOpenDetails, setIsOpenDetails] = useState(false);
    const [details, setDetails] = useState({});

    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    const now  = new Date();
    const startDay = (new Date()).setUTCHours(0,0,0,0);
    const start24h = new Date(new Date().getTime() - (24 * 60 * 60 * 1000));
    const date = now.toLocaleDateString("en-US", options);

    const openDetails = () => {
        setIsOpenDetails(!isOpenDetails);
        (async () => {
            const assetsFetch = await getCurrentCripto(asset.id, startDay, now.getTime());
            dispatch({type: "ADD_ASSETS_TODAY", payload: assetsFetch.data});
            const assets24h = await getCurrentCripto(asset.id, start24h.getTime(), now.getTime());
            dispatch({type: "ADD_ASSETS_24h", payload: assets24h.data});
            dispatch({type: "SET_IS_OPEN", payload: asset.id});
        })();
    }

    useEffect(() => {
        assetsToday.length && setDetails(getDetails(assetsToday));
    }, [assetsToday]);


        const formatYAxis = (tickItem) => {
            return Number(tickItem).toFixed();
        };

        const formatXAxis = (tickItem) => {
            const hour = (new Date(tickItem)).getHours();
            return `${hour}h`;
        }

    return (
        <>
            <tr onClick={openDetails}>
                <td>{asset.rank}</td>
                <td>{asset.name}</td>
                <td>${roundSeparateNumber(asset.priceUsd)}</td>
                <td>${roundSeparateNumber(asset.marketCapUsd)}</td>
                <td>${roundSeparateNumber(asset.vwap24Hr)}</td>
                <td>${roundSeparateNumber(asset.supply)}</td>
                <td>${roundSeparateNumber(asset.volumeUsd24Hr)}</td>
                <td>{roundSeparateNumber(asset.changePercent24Hr)}%</td>
            </tr>
            {(isOpen === asset.id) && <>
                <tr className="subTable__row">
                    <td colSpan={2} rowSpan={2}>
                        <img src={`https://assets.coincap.io/assets/icons/${asset.symbol.toLowerCase()}@2x.png`}/>
                    </td>
                    <td colSpan={2}>
                        {asset.name} ({asset.symbol})
                    </td>
                    <td colSpan={2}>HIGH: ${roundSeparateNumber(details.max)}</td>
                    <td colSpan={2} rowSpan={2} className="align-middle">AVERAGE: ${roundSeparateNumber(details.average)}</td>
                </tr>
                    <tr className="subTable__row">
                        <td colSpan={2}>{date}</td>
                        <td colSpan={2}>LOW: ${roundSeparateNumber(details.min)}</td>
                    </tr>
                <tr>
                    { assets24h.length && <LineChart
                        width={800}
                        height={400}
                        data={assets24h}
                        margin={{ top: 5, right: 20, left: 10, bottom: 5 }}
                        useInteractiveGuideline={true}
                    >
                        <XAxis dataKey="date" tickFormatter={formatXAxis}/>
                        <YAxis showMaxMin={false} tickCount={20} tickFormatter={formatYAxis} type="number" domain={[details.min, details.max]} />
                        <Tooltip />
                        <CartesianGrid strokeDasharray="3 3" />
                        <Line activeDot={{ r: 8 }} type="monotone" dataKey="priceUsd" stroke="#ff7300"  />
                         />
                    </LineChart>}
                </tr>
                </>
                }
            </>
                )
            }
;

export default ItemTableCurrency;
