import React from 'react';
import {ListGroup} from "react-bootstrap";
import {useSelector} from "react-redux";

const Header = () => {
    const {assets} = useSelector(state => state.assets);
    const popularCripto = assets.slice(0, 3);

    return (
        <ListGroup horizontal>
            {popularCripto.map((cripto) =>
                <ListGroup.Item className="border-0">{cripto.name} ({cripto.symbol})</ListGroup.Item>
            )}
            <ListGroup.Item className="border-0">{popularCripto}</ListGroup.Item>
            <ListGroup.Item className="border-0">ListGroup</ListGroup.Item>
            <ListGroup.Item className="border-0">renders</ListGroup.Item>
            <ListGroup.Item className="border-0">horizontally!</ListGroup.Item>
        </ListGroup>
    )
};

export default Header;
