import axios from "axios";
import {addAssets, setIsOpenAsset, setPage} from "../store/assetReducer";

const getCripto =  (page, limit) => {
        return async function (dispatch) {
            const response = await axios.get('https://api.coincap.io/v2/assets', {
                params: {
                    limit: limit,
                    offset: (page - 1) * limit,
                }
            });
            return dispatch(addAssets(response.data.data));
        }
};

const getCurrentCripto = async (id, start, end) => {
    try {
        const response = await axios.get(`https://api.coincap.io/v2/assets/${id}/history`, {
            params: {
                interval: 'h1',
                start: start,
                end: end,
            }
        });
        return response.data;
    } catch (e) {
        console.log(e);
        return 0;
    }
};

export {getCripto, getCurrentCripto};
