import React from "react";
import {useDispatch, useSelector} from "react-redux";
import {Button, FormControl, InputGroup, Modal} from "react-bootstrap";

const AddingCurrency = ({currency}) => {
    const {isOpenAddingWallet, addedCurrency} = useSelector(state => state.wallet);
    const dispatch = useDispatch();

    const closeWallet = () => {
        dispatch({type: "SET_IS_OPEN_ADDING_WALLET", payload: false});
    };

    const addToWallet = () => {
console.log(addedCurrency)
        closeWallet();
    };

    return (
        <Modal
            show={isOpenAddingWallet}
            onHide={closeWallet}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title>Add currency to the wallet</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <h4>{addedCurrency.name}</h4>
                <InputGroup className="mb-3">
                    <InputGroup.Text id="basic-addon1">Count: </InputGroup.Text>
                    <FormControl
                        placeholder="Count of currency"
                        aria-label="Count"
                    />
                </InputGroup>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="primary" onClick={addToWallet}>
                    Save Changes
                </Button>
                <Button variant="secondary" onClick={closeWallet}>
                    Close
                </Button>
            </Modal.Footer>
        </Modal>
    )
};

export default AddingCurrency;
