                end: end,
            }
        });
        console.log(id, isDetails)
        if (isDetails) {
            console.log(getDetails(response.data.data))
            dispatch(addDetails(getDetails(response.data.data)));
        } else dispatch(addAssets24(response.data.data));
    } catch (error) {
        console.log(error);
    }


