import '../../sass/abstracts/_variables.scss';
import Main from "../main/Main.component";
import 'bootstrap/dist/css/bootstrap.min.css';
import Header from "../header/Header.component";
import React from "react";
import Wallet from "../wallet/Wallet.component";
import AddingCurrency from "../main/components/AddingCurrency.component";
import {BrowserRouter as Router, Route, Switch} from "react-router-dom";
import CurrencyPage from "../currencyPage/CurrencyPage.component";
import TableCurrency from "../main/components/Table.component";
import PaginationBlock from "../main/components/Pagination.component";
import Loader from "../main/components/Loader.component";
import {useSelector} from "react-redux";

const App = () => {
    const {assets, activePage, limit, isOpenAssets} = useSelector(state => state.assets);

    if (window.performance) {
        if (performance.navigation.type == 1) {
            alert("This page is reloaded");
        } else {
            alert("This page is not reloaded");
        }
    }
    return (
        <Router>

        <Header/>
            <Wallet/>
            <AddingCurrency/>
            <Switch>
                {assets.map((item) => (
                    <Route
                        key={item.id}
                        path={"/currency/:id"}
                        render={() => <CurrencyPage />}
                    />
                ))}
                <Route path="/">
                   <Main />
                </Route>
            </Switch>        </Router>
    );
}

export default App;
