import axios from "axios";
import {addAssets} from "../store/assetReducer";
import {addAssets24, addDetails} from "../store/detailsReducer";

const getCripto =  (page, limit) => {
        return async function (dispatch) {
            const response = await axios.get('https://api.coincap.io/v2/assets', {
                params: {
                    limit: limit,
                    offset: (page - 1) * limit,
                }
            });
            return dispatch(addAssets(response.data.data));
        }
};

const getCurrentCripto = async (id, start, end, isDetails) => {
    return async function (dispatch) {
        const response = await axios.get(`https://api.coincap.io/v2/assets/${id}/history`, {
            params: {
                interval: 'h1',
                start: start,
                end: end,
            }
        });
        if (isDetails) return dispatch(addDetails(response.data.data));
        return dispatch(addAssets24(response.data.data));
    }
