import React from 'react';
import {Badge, ListGroup} from "react-bootstrap";
import {useSelector} from "react-redux";
import {roundSeparateNumber} from "../../common/utils";

const Header = () => {
    const {assets} = useSelector(state => state.assets);
    const popularCripto = assets.slice(0, 3);

    return (
        <ListGroup horizontal>
            {popularCripto.map((cripto) =>
                <ListGroup.Item className="border-0 " key={cripto.symbol}>
                    {cripto.name} ({cripto.symbol})
                    <Badge bg="info">${roundSeparateNumber(cripto.marketCapUsd)}</Badge>
                </ListGroup.Item>
            )}
        </ListGroup>
    )
};

export default Header;
